import string

def caesar(text, offset):
    decrypted = ''
    for letter in text:
        if letter == ' ':
            decrypted += ' '
        else:
            letter = letter.lower()
            i = ord(letter) + offset
            if i > 122:
                i -= 26
            decrypted += chr(i)
    return decrypted

def caesar2(plaintext, shift):
    alphabet = string.ascii_lowercase
    shifted_alphabet = alphabet[shift:] + alphabet[:shift]
    table = string.maketrans(alphabet, shifted_alphabet)
    return plaintext.translate(table)

for i in range(26):
    d = caesar2(text, i)
    if 'the' in d.split():
        print(d, i)
        break
            
